public static string CompareTwoLists_ReturnDifferences<T1, T2>(List<T1> List1, List<T2> List2)\n    where T1 : class\n    where T2 : class\n{\n    if (List1.Count != List2.Count)\n        throw new ArgumentException("Lists must have the same number of elements.");\n\n    var Differences = CoreFormat.StringNoCharacters;\n\n    for (int i = 0; i < List1.Count; i++)\n    {\n        var diff = CompareTwoClass_ReturnDifferences(List1[i], List2[i]);\n        if (diff != CoreFormat.StringNoCharacters)\n        {\n            Differences = Differences == CoreFormat.StringNoCharacters\n                ? string.Format("Element {0}: {1}", i, diff)\n                : string.Format("{0} {1}Element {2}: {3}", Differences, Environment.NewLine, i, diff);\n        }\n    }\n\n    return Differences;\n}