using System;\nusing System.Net;\n\nclass Program\n{\n    static void Main()\n    {\n        using (var webClient = new WebClient())\n        {\n            string url = "http://example.com/versions/";\n\n            // Get the list of directories\n            string directoryList = webClient.DownloadString(url);\n\n            // Split the directory list into individual directory names\n            string[] directories = directoryList.Split(new[] { Environment.NewLine }, StringSplitOptions.RemoveEmptyEntries);\n\n            // Find the latest version\n            string latestVersion = GetLatestVersion(directories);\n\n            Console.WriteLine("Latest version: " + latestVersion);\n        }\n    }\n\n    static string GetLatestVersion(string[] directories)\n    {\n        Version latestVersion = null;\n\n        foreach (string directory in directories)\n        {\n            if (Version.TryParse(directory, out Version version))\n            {\n                if (latestVersion == null || version > latestVersion)\n                {\n                    latestVersion = version;\n                }\n            }\n        }\n\n        return latestVersion.ToString();\n    }\n}