using System;\nusing System.Runtime.InteropServices;\n\npublic class BrightnessController\n{\n    [DllImport("user32.dll")]\n    static extern bool SetMonitorBrightness(IntPtr hMonitor, int dwBrightness);\n\n    public static void SetBrightness(int brightness)\n    {\n        // Get the handle to the primary monitor\n        IntPtr hMonitor = NativeMethods.MonitorFromWindow(IntPtr.Zero, NativeMethods.MONITOR_DEFAULTTOPRIMARY);\n\n        // Set the brightness\n        SetMonitorBrightness(hMonitor, brightness);\n    }\n}\n\ninternal static class NativeMethods\n{\n    [DllImport("user32.dll")]\n    public static extern IntPtr MonitorFromWindow(IntPtr hwnd, uint dwFlags);\n\n    public const uint MONITOR_DEFAULTTOPRIMARY = 1;\n}