// Get the DataView of the Product DataTable\nDataView dv = ds.Tables["Product"].DefaultView;\n\n// Filter the DataView based on related tables\nforeach (DataRow row in ds.Tables["Product"].Rows)\n{\n    DataRow[] childRows = row.GetChildRows("Product_Feature");\n    DataRow[] supplierRows = row.GetChildRows("Product_Supplier");\n\n    bool hasWidthFeature = childRows.Any(r => r["Name"].ToString() == "Width");\n    bool hasMicrosoftSupplier = supplierRows.Any(r => r["Name"].ToString() == "Microsoft");\n\n    if (hasWidthFeature && hasMicrosoftSupplier)\n    {\n        // Add row to the result DataView\n        dv.AddNew(row.ItemArray);\n    }\n}