using System.DirectoryServices.Protocols;\n\n// Define the LDAP connection string\nstring ldapConnectionString = "LDAP://your-ldap-server.com";\n\n// Create an LdapConnection object\nLdapConnection connection = new LdapConnection(ldapConnectionString);\n\n// Define the username and password to authenticate\nstring username = "username";\nstring password = "password";\n\n// Define the service account credentials (if required)\nstring serviceAccountUsername = "service-account-username";\nstring serviceAccountPassword = "service-account-password";\n\n// Bind to the LDAP server using the service account credentials (if required)\nif (!string.IsNullOrEmpty(serviceAccountUsername) && !string.IsNullOrEmpty(serviceAccountPassword))\n{\n    connection.Credential = new NetworkCredential(serviceAccountUsername, serviceAccountPassword);\n    connection.Bind();\n}\n\n// Authenticate the user\nconnection.Credential = new NetworkCredential(username, password);\nconnection.Bind();\n\n// If no exceptions are thrown, the user is authenticated