using System;\nusing System.Runtime.InteropServices;\n\nclass VpnConnection\n{\n    [DllImport("rasapi32.dll")]\n    static extern int RasSetEntryProperties(string phonebook, string entry, IntPtr buffer, int bufferSize);\n\n    [DllImport("rasapi32.dll")]\n    static extern int RasAddEntry(string phonebook, string entry, IntPtr buffer, int bufferSize);\n\n    [StructLayout(LayoutKind.Sequential, CharSet = CharSet.Unicode)]\n    public struct RASENTRY\n    {\n        public int dwSize;\n        public int dwfOptions;\n        public string szLocalPhoneNumber;\n        public string szAlternatePhoneNumber;\n        public string szCallbackNumber;\n        public string szUserName;\n        public string szPassword;\n        public string szDomain;\n        public int dwRedialCount;\n        public int dwRedialPause;\n        public int dwLcpOptions;\n        public int dwBps;\n        public int dwEncryptionType;\n        public int dwCustomAuthKey;\n        public int dwCustomAuthDataSize;\n        public IntPtr CustomAuthData;\n    }\n\n    public static void CreateVpnConnection(string name, string serverAddress, string psk)\n    {\n        RASENTRY entry = new RASENTRY();\n        entry.dwSize = Marshal.SizeOf(entry);\n        entry.dwfOptions = 0x1000; // RASEO_RemoteDefaultGateway\n        entry.szLocalPhoneNumber = serverAddress;\n        entry.szUserName = name;\n        entry.szPassword = psk;\n\n        IntPtr buffer = Marshal.AllocHGlobal(Marshal.SizeOf(entry));\n        Marshal.StructureToPtr(entry, buffer, true);\n\n        int result = RasAddEntry(null, name, buffer, Marshal.SizeOf(entry));\n        if (result != 0)\n        {\n            Console.WriteLine("Error creating VPN connection: " + result);\n        }\n\n        Marshal.FreeHGlobal(buffer);\n    }\n\n    public static void Main()\n    {\n        CreateVpnConnection("MyVPN", "vpn.example.com", "myPsk");\n    }\n}