// assume you have a DataSet and a DataAdapter\nDataSet dataSet = new DataSet();\nSqlDataAdapter dataAdapter = new SqlDataAdapter();\n\n// create a SqlCommand to call the stored procedure\nSqlCommand updateCommand = new SqlCommand("sprocUpdate", connection);\nupdateCommand.CommandType = CommandType.StoredProcedure;\nupdateCommand.Parameters.AddWithValue("@ColumnName", ColumnName);\nupdateCommand.Parameters.AddWithValue("@NewValue", NewValue);\nupdateCommand.Parameters.AddWithValue("@ID", ID);\n\n// add the update command to the DataAdapter\ndataAdapter.UpdateCommand = updateCommand;\n\n// update the DataSet\ndataAdapter.Update(dataSet);