using System;\nusing System.Web;\n\npublic class DateTimeHelper\n{\n    public static DateTime ConvertUtcToLocal(DateTime utcDateTime, string timeZoneId)\n    {\n        TimeZoneInfo tz = TimeZoneInfo.FindSystemTimeZoneById(timeZoneId);\n        return TimeZoneInfo.ConvertTimeFromUtc(utcDateTime, tz);\n    }\n}\n\n// In your ASP.NET page\nDateTime utcDateTime = DateTime.UtcNow;\nstring timeZoneId = "Eastern Standard Time"; // Replace with the user's time zone ID\nDateTime localDateTime = DateTimeHelper.ConvertUtcToLocal(utcDateTime, timeZoneId);\n\n// Display the local date and time\nResponse.Write(localDateTime.ToString("yyyy-MM-dd HH:mm:ss"));