Process msBuild = new Process();\nmsBuild.StartInfo.FileName = "Build.bat";\nmsBuild.StartInfo.UseShellExecute = false;\nmsBuild.StartInfo.RedirectStandardOutput = true;\nmsBuild.Start();\nstring output = "";\nTask task = Task.Run(() =>\n{\n    output = msBuild.StandardOutput.ReadToEnd();\n});\nwhile (!msBuild.HasExited)\n{\n    Console.WriteLine(msBuild.StandardOutput.ReadLine());\n}\nmsBuild.WaitForExit();\ntask.Wait();