using Gtk;\n\npublic class MyWindow : Window\n{\n    private ScrolledWindow scrolledWindow;\n\n    public MyWindow() : base("My Window")\n    {\n        scrolledWindow = new ScrolledWindow();\n        // ...\n\n        // Connect to the ValueChanged event of the adjustments\n        scrolledWindow.HAdjustment.ValueChanged += OnScrollEvent;\n        scrolledWindow.VAdjustment.ValueChanged += OnScrollEvent;\n    }\n\n    private void OnScrollEvent(object sender, EventArgs e)\n    {\n        // Run your code here when the scrolled window is scrolled\n        Console.WriteLine("Scrolled!");\n    }\n}