using System.Security.AccessControl;\nusing System.Security.Cryptography.X509Certificates;\n\n// Load the certificate\nX509Certificate2 certificate = new X509Certificate2("Debug.cer");\n\n// Get the private key\nRSACryptoServiceProvider privateKey = certificate.PrivateKey as RSACryptoServiceProvider;\n\n// Grant access to the private key\nCryptoKeySecurity cryptoKeySecurity = new CryptoKeySecurity();\ncryptoKeySecurity.AddAccessRule(new CryptoKeyAccessRule("NT AUTHORITY\\NETWORK SERVICE", CryptoKeyRights.GenericRead, AccessControlType.Allow));\nprivateKey.CspKeyContainerInfo.CryptoKeySecurity = cryptoKeySecurity;