using Gtk;\n\npublic class FileChooserDialogExample\n{\n    public static void Main(string[] args)\n    {\n        Application.Init();\n\n        FileChooserDialog dialog = new FileChooserDialog("Select File or URI", null, FileChooserAction.Open);\n        dialog.LocalOnly = false;\n\n        // Get the text entry and connect to the "changed" signal\n        Entry entry = (Entry)dialog.Children[1];\n        entry.Changed += (sender, e) =>\n        {\n            string text = entry.Text;\n            if (text.StartsWith("http://", StringComparison.OrdinalIgnoreCase) ||\n                text.StartsWith("https://", StringComparison.OrdinalIgnoreCase) ||\n                text.StartsWith("ftp://", StringComparison.OrdinalIgnoreCase))\n            {\n                // Set the current file name to the URI\n                dialog.SetCurrentName(text);\n            }\n        };\n\n        // Show the dialog and run the application\n        dialog.ShowAll();\n        Application.Run();\n    }\n}