using iTextSharp.text.pdf;\n\npublic void RemoveHyperlinks(byte[] pdfBytes)\n{\n    using (var ms = new MemoryStream(pdfBytes))\n    {\n        var pdfReader = new PdfReader(ms);\n        var pdfStamper = new PdfStamper(pdfReader, new MemoryStream());\n\n        for (int i = 1; i <= pdfReader.NumberOfPages; i++)\n        {\n            var annotations = pdfReader.GetPageN(i).GetAnnotations();\n            foreach (var annotation in annotations)\n            {\n                if (annotation is PdfAnnotation link)\n                {\n                    link.SetAction(PdfAction.JAVASCRIPT("this.pageNum=" + i, pdfStamper.Writer));\n                    link.Border = new PdfBorderArray(0, 0, 0, 0);\n                    link.BorderStyle = new PdfBorderDictionary(0, PdfBorderDictionary.STYLE_SOLID);\n                }\n            }\n        }\n\n        pdfStamper.FormFlattening = true;\n\n        var result = new byte[pdfStamper.OutputStream.ToArray().Length];\n        pdfStamper.OutputStream.ToArray().CopyTo(result);\n        pdfStamper.Close();\n        pdfReader.Close();\n\n        // Use the result byte array, which contains the PDF with removed hyperlinks\n    }\n}