using RegistryLinq;\n\n// Create a new RegistryContext instance\nRegistryContext ctx = new RegistryContext();\n\n// Query the registry\nvar results = from key in ctx.Keys("HKEY_CURRENT_USER\\Software\\MyApp")\n              select new { KeyName = key.Name, KeyValue = key.Value };\n\n// Iterate over the results\nforeach (var result in results)\n{\n    Console.WriteLine(result.KeyName + ": " + result.KeyValue);\n}