// Example of querying in EF Core\nusing Microsoft.EntityFrameworkCore;\nusing System.Linq;\n\npublic class MyDbContext : DbContext\n{\n    public DbSet<MyEntity> MyEntities { get; set; }\n\n    protected override void OnConfiguring(DbContextOptionsBuilder optionsBuilder)\n    {\n        optionsBuilder.UseSqlServer(@"Data Source=YourServer;Initial Catalog=YourDatabase;User ID=YourUser;Password=YourPassword;");\n    }\n}\n\npublic class MyEntity\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n}\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        using var context = new MyDbContext();\n\n        var entities = context.MyEntities\n            .Where(e => e.Name.Contains("specificName"))\n            .ToList();\n\n        // Do something with the entities\n    }\n}