// Using PerfView to collect ETW events\nusing System;\nusing System.Diagnostics;\n\nclass MyGUIApp\n{\n    static void Main(string[] args)\n    {\n        // Start collecting ETW events\n        var collector = new PerfViewCollector();\n        collector.Start();\n\n        // Run your GUI application...\n        Application.Run(new MyGUIForm());\n\n        // Stop collecting ETW events\n        collector.Stop();\n    }\n}