var forums = (from f in db.Forums\n              join ft in db.ForumThreads on f.ForumId equals ft.ForumId\n              join fp in db.ForumPosts on ft.ThreadId equals fp.ThreadId\n              group new { ft, fp } by f into g\n              select new\n              {\n                  Forum = g.Key.Title,\n                  Topics = g.Select(x => x.ft.ThreadId).Distinct().Count(),\n                  Posts = g.Select(x => x.fp.PostId).Count(),\n                  LastPostUserId = g.OrderByDescending(x => x.fp.CreateDate).First().fp.UserId,\n                  LastPostTime = g.OrderByDescending(x => x.fp.CreateDate).First().fp.CreateDate\n              }).ToList();