// Assuming 'sharedDS' is your DataSet and 'Table1' is the DataTable\n// you want to reset the primary key for.\n\nsharedDS.Table1.PrimaryKey = new DataColumn[0]; // Reset the primary key\n\n// Clean the DataTable\nsharedDS.Table1.Clear();\n\n// If you want to reset the auto-incrementing ID column (if any)\nforeach (DataColumn column in sharedDS.Table1.Columns)\n{\n    if (column.AutoIncrement)\n    {\n        column.AutoIncrementSeed = 1;\n        column.AutoIncrementStep = 1;\n    }\n}