[TestMethod]\npublic void GetProduct_ValidId_ReturnsProduct()\n{\n    // Arrange\n    var product = new Product { Name = "Test Product" };\n    var productId = _productDal.Create(product);\n\n    // Act\n    var retrievedProduct = _productDal.Get(productId);\n\n    // Assert\n    Assert.AreEqual(product.Name, retrievedProduct.Name);\n\n    // Cleanup\n    _productDal.Delete(productId);\n}