using System.Windows.Media.Media3D;\n\n// Create a 3D model\nModel3D model = new Model3D();\nmodel.Content = new GeometryModel3D(\n    new MeshGeometry3D(\n        new Point3D[] { new Point3D(0, 0, 0), new Point3D(1, 0, 0), new Point3D(0, 1, 0) },\n        new Vector3D[] { new Vector3D(0, 0, 1), new Vector3D(0, 1, 0), new Vector3D(1, 0, 0) }\n    ),\n    new DiffuseMaterial(new SolidColorBrush(Colors.Red))\n);\n\n// Create a 3D view\nView3D view = new View3D();\nview.Model = model;\n\n// Add the 3D view to a WPF window\nWindow window = new Window();\nwindow.Content = view;\nwindow.ShowDialog();