using System.Net.Http;\nusing System.Web;\n\n// assuming you have the form data in a dictionary\nvar formData = new Dictionary<string, string> { { "field1", "value1" }, { "field2", "value2" } };\n\n// create an instance of HttpClient\nvar client = new HttpClient();\n\n// set the request headers (e.g., User-Agent, Accept, etc.)\nclient.DefaultRequestHeaders.Add("User-Agent", "Mozilla/5.0");\n\n// create the request content (form data)\nvar content = new FormUrlEncodedContent(formData);\n\n// send the request to the legacy website\nvar response = await client.PostAsync("https://legacywebsite.com/submit", content);\n\n// check the response status code\nif (response.IsSuccessStatusCode)\n{\n    // submission successful\n}\nelse\n{\n    // handle errors\n}