protected void Page_Load(object sender, EventArgs e)\n{\n    DropDownList ddlTime = new DropDownList();\n    ddlTime.DataSource = GetTimeIntervals();\n    ddlTime.DataBind();\n}\n\nprivate List<string> GetTimeIntervals()\n{\n    List<string> timeIntervals = new List<string>();\n    DateTime startTime = DateTime.Parse("8:00 AM");\n    DateTime endTime = DateTime.Parse("7:00 PM");\n    while (startTime <= endTime)\n    {\n        timeIntervals.Add(startTime.ToString("hh:mm tt"));\n        startTime = startTime.AddMinutes(15);\n    }\n    return timeIntervals;\n}