// Connection string\nstring connectionString = "Provider=Microsoft.Jet.OLEDB.4.0;Data Source=C:\\Path\\To\\File.csv;Extended Properties=\"Text;HDR=YES;FMT=CSVDelimited\"";\n\n// Create a new OleDbConnection\nusing (OleDbConnection connection = new OleDbConnection(connectionString))\n{\n    // Open the connection\n    connection.Open();\n\n    // Create a new OleDbCommand\n    using (OleDbCommand command = new OleDbCommand("SELECT * FROM File.csv", connection))\n    {\n        // Execute the command\n        using (OleDbDataReader reader = command.ExecuteReader())\n        {\n            // Read the data\n            while (reader.Read())\n            {\n                // Get the values by column name\n                string ourReference = reader["OurReference"].ToString();\n                string client = reader["Client"].ToString();\n                decimal amount = decimal.Parse(reader["Amount"].ToString());\n            }\n        }\n    }\n}