using System;\nusing System.IO;\nusing System.Text;\n\nclass AnsiToAsciiConverter\n{\n    public static void ConvertFile(string ansiFilePath, string asciiFilePath)\n    {\n        // Read the ANSI file\n        string ansiText = File.ReadAllText(ansiFilePath, Encoding.Default);\n\n        // Convert to ASCII\n        byte[] asciiBytes = Encoding.ASCII.GetBytes(ansiText);\n        string asciiText = Encoding.ASCII.GetString(asciiBytes);\n\n        // Write to ASCII file\n        File.WriteAllText(asciiFilePath, asciiText, Encoding.ASCII);\n    }\n}