using System.Web.UI;\nusing Castle.ActiveRecord;\n\npublic class MultiTableExistenceValidator : IValidator\n{\n    public MultiTableExistenceValidator(string[] tableNames, string fieldName)\n    {\n        TableNames = tableNames;\n        FieldName = fieldName;\n    }\n\n    public string[] TableNames { get; private set; }\n    public string FieldName { get; private set; }\n\n    public bool IsValid(object entity)\n    {\n        // Iterate over the tables and check if the field exists\n        foreach (string tableName in TableNames)\n        {\n            if (!ActiveRecordBase.Exists(tableName, FieldName, entity.GetType().GetProperty(FieldName).GetValue(entity, null)))\n            {\n                return false;\n            }\n        }\n        return true;\n    }\n}