using System.Windows.Forms;\nusing System.Data;\n\n// assuming you have a LINQ to SQL or Entities data context\nvar dbContext = new MyDataContext();\n\n// create a BindingSource component\nvar bindingSource = new BindingSource();\n\n// set the data source to your LINQ query\nbindingSource.DataSource = dbContext.MyTable;\n\n// bind the grid to the BindingSource\ndataGridView1.DataSource = bindingSource;\n\n// enable editing\ndataGridView1.ReadOnly = false;