public class FluentConfigurationProvider : IConfigurationProvider\n{\n    public Configuration GetConfiguration()\n    {\n        return Fluently.Configure()\n            .Database(MsSqlConfiguration.MsSql2008.ConnectionString("your_connection_string"))\n            .Mappings(m => m.FluentMappings.AddFromAssembly(typeof(YourEntity).Assembly))\n            .BuildConfiguration();\n    }\n}