using System.Security.Cryptography;\nusing System.IO;\nusing System.Xml;\n\npublic class SecureConfig\n{\n    private static readonly string CONFIG_FILE = "path/to/config.file";\n    private static readonly string PASSWORD = "your_password";\n\n    public static string GetPassPhrase()\n    {\n        return GetSecureValue("PassPhrase");\n    }\n\n    public static string GetSaltValue()\n    {\n        return GetSecureValue("SaltValue");\n    }\n\n    public static string GetInitializationVector()\n    {\n        return GetSecureValue("InitializationVector");\n    }\n\n    private static string GetSecureValue(string key)\n    {\n        XmlDocument xmlDoc = new XmlDocument();\n        xmlDoc.Load(CONFIG_FILE);\n\n        // Decrypt the value using the password\n        string encryptedValue = xmlDoc.SelectSingleNode("//" + key).InnerText;\n        byte[] decryptedBytes = ProtectedData.Unprotect(Convert.FromBase64String(encryptedValue), Convert.FromBase64String(PASSWORD), DataProtectionScope.LocalMachine);\n        return Encoding.UTF8.GetString(decryptedBytes);\n    }\n}