using System;\nusing System.Data;\nusing System.Linq;\nusing Excel = Microsoft.Office.Interop.Excel;\n\nclass Program\n{\n    static void Main()\n    {\n        // assuming 'db' is your DataContext and 'MyTable' is your table\n        var data = from d in db.MyTable\n                   where d.Name == "Test"\n                   select d;\n\n        // Create an Excel application object\n        Excel.Application excelApp = new Excel.Application();\n        excelApp.Visible = true; // Set to false if you don't want to show Excel\n\n        // Create a new workbook and worksheet\n        Excel.Workbook workbook = excelApp.Workbooks.Add();\n        Excel.Worksheet worksheet = workbook.Sheets[1];\n\n        // Set the header row\n        worksheet.Cells[1, 1].Value2 = "Column1";\n        worksheet.Cells[1, 2].Value2 = "Column2";\n        // Add more column headers as necessary\n\n        int row = 2; // Starting row for data\n\n        // Iterate over the LINQ query results\n        foreach (var item in data)\n        {\n            worksheet.Cells[row, 1].Value2 = item.Column1;\n            worksheet.Cells[row, 2].Value2 = item.Column2;\n            // Add more columns as necessary\n\n            row++;\n        }\n\n        // Clean up\n        workbook.SaveAs("output.xlsx");\n        workbook.Close();\n        excelApp.Quit();\n    }\n}