using Moq;\nusing Microsoft.AspNetCore.Authentication;\n\npublic class FakeAuthenticationManager : IAuthenticationManager\n{\n    private readonly Mock<IAuthenticationManager> _mock;\n\n    public FakeAuthenticationManager()\n    {\n        _mock = new Mock<IAuthenticationManager>();\n    }\n\n    public void SignIn(string username, string password)\n    {\n        _mock.Setup(a => a.SignInAsync(username, password)).Returns(Task.CompletedTask);\n    }\n\n    public void SignOut()\n    {\n        _mock.Setup(a => a.SignOutAsync()).Returns(Task.CompletedTask);\n    }\n\n    public ClaimsPrincipal GetCurrentUser()\n    {\n        var claimsPrincipal = new ClaimsPrincipal(new ClaimsIdentity("FakeUser"));\n        _mock.Setup(a => a.GetCurrentUserAsync()).Returns(Task.FromResult(claimsPrincipal));\n        return claimsPrincipal;\n    }\n}