public class FileIOPermission: CodeAccessPermission, IUnrestrictedPermission\n{\n    public override IPermission Union(IPermission target)\n    {\n        if (target is FileIOPermission)\n            return target;\n        else\n            throw new ArgumentException("target", "target is not a FileIOPermission");\n    }\n\n    public override IPermission Intersect(IPermission target)\n    {\n        if (target is FileIOPermission)\n            return Copy();\n        else\n            return null;\n    }\n}