public class DerivedClass : BaseClass\n{\n    // Place the caret here and press Ctrl+K, Ctrl+S to generate the constructors\n}\n\n// Then, in the IntelliSense code snippet manager, add the following snippet:\n\n<?xml version="1.0" encoding="utf-8"?>\n<CodeSnippets xmlns="http://schemas.microsoft.com/VisualStudio/2005/CodeSnippet">\n  <CodeSnippet Format="1.0.0">\n    <Header>\n      <Title>DerivedConstructors</Title>\n      <Shortcut>dc</Shortcut>\n    </Header>\n    <Snippet>\n      <Code Language="CSharp">\n        <![CDATA[\n$baseclass$($params$)\n{\n    : base($params$)\n{\n    $end$\n}\n]]>\n      </Code>\n      <Declarations>\n        <Literal>\n          <ID>baseclass</ID>\n          <Type>System.String</Type>\n          <ToolTip>The name of the base class.</ToolTip>\n          <Default>BaseClass</Default>\n        </Literal>\n        <Literal>\n          <ID>params</ID>\n          <Type>System.String</Type>\n          <ToolTip>The parameters of the constructor.</ToolTip>\n          <Default>params</Default>\n        </Literal>\n      </Declarations>\n    </Snippet>\n  </CodeSnippet>\n</CodeSnippets>