protected void Timer_Tick(object sender, EventArgs e)\n{\n    // Save the current scroll position\n    string script = "var scrollPosition = document.body.scrollTop;";\n    ScriptManager.RegisterStartupScript(this, typeof(Page), "SaveScrollPosition", script, true);\n\n    // Do your timer stuff here...\n\n    // Restore the scroll position after the partial postback\n    script = "document.body.scrollTop = scrollPosition;";\n    ScriptManager.RegisterStartupScript(this, typeof(Page), "RestoreScrollPosition", script, true);\n}