[ServiceContract]\ninterface IService\n{\n    [OperationContract]\n    void SendMessage(string message);\n}\n\nclass Service : IService\n{\n    public void SendMessage(string message)\n    {\n        Console.WriteLine("Received message: " + message);\n    }\n}\n\nclass Program\n{\n    public static void Main()\n    {\n        using (ServiceHost host = new ServiceHost(typeof(Service), new Uri("net.pipe://localhost/MyService")))\n        {\n            host.AddServiceEndpoint(typeof(IService), new NetNamedPipeBinding(), "");\n            host.Open();\n            Console.WriteLine("Service is running");\n            // ...\n        }\n    }\n}\n\nclass Client\n{\n    public static void Main()\n    {\n        ChannelFactory<IService> factory = new ChannelFactory<IService>(new NetNamedPipeBinding(), "net.pipe://localhost/MyService");\n        IService service = factory.CreateChannel();\n        service.SendMessage("Hello from client!");\n    }\n}