using System;\nusing System.Windows.Forms;\n\nclass Program\n{\n    private static NotifyIcon notifyIcon;\n\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n\n        // Create a new NotifyIcon\n        notifyIcon = new NotifyIcon();\n        notifyIcon.Icon = System.Drawing.SystemIcons.Information;\n        notifyIcon.Text = "My Console Application";\n        notifyIcon.Visible = true;\n\n        // Add a context menu to the NotifyIcon\n        ContextMenuStrip contextMenuStrip = new ContextMenuStrip();\n        ToolStripMenuItem toolStripMenuItem = new ToolStripMenuItem("Exit");\n        toolStripMenuItem.Click += ToolStripMenuItem_Click;\n        contextMenuStrip.Items.Add(toolStripMenuItem);\n        notifyIcon.ContextMenuStrip = contextMenuStrip;\n\n        // Run the application\n        Application.Run(new MyConsoleApplication());\n    }\n\n    private static void ToolStripMenuItem_Click(object sender, EventArgs e)\n    {\n        Application.Exit();\n    }\n}\n\npublic class MyConsoleApplication : Form\n{\n    public MyConsoleApplication()\n    {\n        // Set the form to be minimized to the system tray\n        this.WindowState = FormWindowState.Minimized;\n        this.ShowInTaskbar = false;\n    }\n}