using System;\nusing System.Collections.Generic;\nusing System.Globalization;\n\npublic class CultureInfoComparer : IComparer<string>\n{\n    private readonly CultureInfo cultureInfo;\n\n    public CultureInfoComparer(CultureInfo cultureInfo)\n    {\n        this.cultureInfo = cultureInfo;\n    }\n\n    public int Compare(string x, string y)\n    {\n        return string.Compare(x, y, cultureInfo, CompareOptions.None);\n    }\n}\n\n// Usage:\nvar comparer = new CultureInfoComparer(CultureInfo.CreateSpecificCulture("en-US"));\nArray.Sort(words, comparer);\nint index = Array.BinarySearch(words, "searchWord", comparer);