using OpenQA.Selenium;\nusing OpenQA.Selenium.Chrome;\nusing System;\nusing System.IO;\n\nclass ScreenshotTaker\n{\n    public static void TakeScreenshot(string url, string buttonSelector, string screenshotPath)\n    {\n        using (IWebDriver driver = new ChromeDriver())\n        {\n            driver.Navigate().GoToUrl(url);\n            driver.FindElement(By.CssSelector(buttonSelector)).Click();\n            Screenshot screenshot = ((ITakesScreenshot)driver).GetScreenshot();\n            screenshot.SaveAsFile(screenshotPath, ScreenshotImageFormat.Png);\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        string url = "https://example.com";\n        string buttonSelector = "#myButton";\n        string screenshotPath = "screenshot.png";\n        TakeScreenshot(url, buttonSelector, screenshotPath);\n    }\n}