using System;\nusing System.IO;\n\nclass AnsiToAsciiConverter\n{\n    public static void ConvertFile(string ansiFilePath, string asciiFilePath)\n    {\n        using (FileStream ansiStream = new FileStream(ansiFilePath, FileMode.Open))\n        {\n            using (BinaryReader reader = new BinaryReader(ansiStream, Encoding.Default))\n            {\n                // Read the ANSI file\n                byte[] ansiBytes = new byte[ansiStream.Length];\n                reader.Read(ansiBytes, 0, ansiBytes.Length);\n\n                // Convert to ASCII\n                byte[] asciiBytes = Encoding.ASCII.GetBytes(Encoding.Default.GetString(ansiBytes));\n\n                // Write to ASCII file\n                using (FileStream asciiStream = new FileStream(asciiFilePath, FileMode.Create))\n                {\n                    asciiStream.Write(asciiBytes, 0, asciiBytes.Length);\n                }\n            }\n        }\n    }\n}