XPathExpression query = xPathNavigator.Compile(xpath);\nXPathNavigator node = xPathNavigator.SelectSingleNode(query.Expression, xmlNamespaceManager);\n\n// Create a new XmlNode with the CDATA section\nXmlNode propertyNode = xmlDoc.CreateElement("Property");\npropertyNode.SetAttribute("name", "someName");\npropertyNode.SetAttribute("type", "String");\n\nXmlNode cdataNode = xmlDoc.CreateCDataSection("someValue");\npropertyNode.AppendChild(cdataNode);\n\n// Append the property node to the parent node\nnode.AppendChild(propertyNode);