using System;\nusing System.Collections;\nusing System.Configuration.Install;\n\nnamespace MyService.Uninstall\n{\n    [RunInstaller(true)]\n    public partial class UninstallService : Installer\n    {\n        public UninstallService()\n        {\n            InitializeComponent();\n        }\n\n        public override void Uninstall(IDictionary savedState)\n        {\n            base.Uninstall(savedState);\n            // Uninstall the service here\n            using (System.ServiceProcess.ServiceController svc = new System.ServiceProcess.ServiceController("MyService"))\n            {\n                svc.Stop();\n                svc.WaitForStatus(System.ServiceProcess.ServiceControllerStatus.Stopped);\n                svc.Delete();\n            }\n        }\n    }\n}