public void InsertCostumer(string name, int age, string address)\n{\n    using (var command = this.myDataContext.Connection.CreateCommand())\n    {\n        command.CommandText = "insert into Customers(name, address) values (@name, @age, @address)";\n\n        var parameterName = new SqlParameter("@name", SqlDbType.NVarChar) { Value = name };\n        var parameterAge = new SqlParameter("@age", SqlDbType.Int) { Value = age };\n        var parameterAddress = new SqlParameter("@address", SqlDbType.NVarChar) { Value = address ?? (object)DBNull.Value };\n\n        command.Parameters.Add(parameterName);\n        command.Parameters.Add(parameterAge);\n        command.Parameters.Add(parameterAddress);\n\n        command.ExecuteNonQuery();\n    }\n}