void testService_DownloadStringCompleted(object sender, DownloadStringCompletedEventArgs e)\n{\n    if (e.Error != null)\n    {\n        if (e.Error is SecurityException)\n        {\n            SecurityException securityException = (SecurityException)e.Error;\n            if (securityException.InnerException != null)\n            {\n                // Log or display the inner exception message\n                string innerExceptionMessage = securityException.InnerException.Message;\n            }\n        }\n    }\n}