using Microsoft.AnalysisServices.AdomdClient;\n\n// create a connection to the cube\nAdomdConnection connection = new AdomdConnection("Data Source=<your_cube_server>;Initial Catalog=<your_cube_name>");\nconnection.Open();\n\n// execute the MDX query\nAdomdCommand command = new AdomdCommand("SELECT [Measures].[Measure1] ON COLUMNS FROM [CubeName]", connection);\nAdomdDataReader reader = command.ExecuteReader();\n\n// use LINQ to process the results\nvar results = reader.Cast<AdomdDataReader>()\n    .Select(r => new { Measure1 = r[0].ToString() })\n    .ToList();\n\n// close the connection\nconnection.Close();