// Create the request\nWebRequest request = HttpWebRequest.Create(button.CommandArgument);\nrequest.Credentials = CredentialCache.DefaultCredentials;\nrequest.PreAuthenticate = true;\n\n// Get the response\nWebResponse response = request.GetResponse();\n\n// Send the response directly to output\nResponse.ContentType = response.ContentType;\nResponse.AppendHeader("Content-Disposition", $"attachment; filename={Path.GetFileName(button.CommandArgument)}");\n\nusing (Stream responseStream = response.GetResponseStream())\n{\n    byte[] buffer = new byte[4096];\n    int bytesRead;\n    while ((bytesRead = responseStream.Read(buffer, 0, buffer.Length)) > 0)\n    {\n        Response.OutputStream.Write(buffer, 0, bytesRead);\n    }\n}\nResponse.End();