public class HtmlString\n{\n    private readonly string _encodedValue;\n\n    public HtmlString(string value)\n    {\n        _encodedValue = HttpUtility.HtmlEncode(value);\n    }\n\n    public override string ToString()\n    {\n        return _encodedValue;\n    }\n}\n\npublic interface IHtmlEncoded\n{\n    void Render(HtmlString value);\n}\n\npublic class MyControl : Control, IHtmlEncoded\n{\n    public void Render(HtmlString value)\n    {\n        // Only accept HtmlString instances\n        base.Render(value.ToString());\n    }\n}