using System;\nusing TwainDotNet;\n\nclass ScannerListener\n{\n    private readonly TwainSession _twainSession;\n\n    public ScannerListener()\n    {\n        _twainSession = new TwainSession();\n        _twainSession.Scanned += TwainSession_Scanned;\n    }\n\n    private void TwainSession_Scanned(object sender, ScannedEventArgs e)\n    {\n        // Process the scanned image here\n        Console.WriteLine("Scan completed!");\n    }\n\n    public void StartListening()\n    {\n        _twainSession.Start();\n    }\n\n    public void StopListening()\n    {\n        _twainSession.Stop();\n    }\n}