public class Consumer\n{\n    private List<IRandomNumberGenerator> generators;\n    private List<double> seeds;\n\n    [Import]\n    public ExportFactory<IRandomNumberGeneratorFactory> GeneratorFactory { get; set; }\n\n    public Consumer()\n    {\n        seeds = new List<double>(new[] { 1.0, 2.0, 3.0 });\n        generators = new List<IRandomNumberGenerator>();\n\n        foreach (var seed in seeds)\n        {\n            var factory = GeneratorFactory.CreateExport();\n            var generator = factory.Value.Create(seed);\n            generators.Add(generator);\n        }\n    }\n}