[Export(typeof(IRandomNumberGenerator))]\npublic class ConcreteRNG : IRandomNumberGenerator\n{\n    private double _seed;\n\n    public ConcreteRNG(double seed)\n    {\n        _seed = seed;\n    }\n\n    // existing methods\n}\n\n[Export(typeof(IRandomNumberGeneratorFactory))]\npublic class ConcreteRNGFactory : IRandomNumberGeneratorFactory\n{\n    public IRandomNumberGenerator Create(double seed)\n    {\n        return new ConcreteRNG(seed);\n    }\n}