public class MyProcedure\n{\n    private readonly StoredProcedureCaller _caller;\n\n    public MyProcedure(StoredProcedureCaller caller)\n    {\n        _caller = caller;\n    }\n\n    public int CallMyProcedure(int param1, string param2)\n    {\n        var parameters = new SqlParameter[]\n        {\n            new SqlParameter("@param1", param1),\n            new SqlParameter("@param2", param2)\n        };\n\n        return _caller.CallProcedure<int>("MyProcedure", parameters);\n    }\n}