public IQueryable<X> GetSortedQuery(DataContext db, string sortColumn, bool sortDesc)\n{\n    var query = db.GetTable<X>().AsQueryable();\n\n    if (sortDesc)\n    {\n        query = query.OrderByDescending(x => x.GetType().GetProperty(sortColumn).GetValue(x, null));\n    }\n    else\n    {\n        query = query.OrderBy(x => x.GetType().GetProperty(sortColumn).GetValue(x, null));\n    }\n\n    return query;\n}